        "evm_version" => "default",
        "name" => "ContractFromFactory",
        "optimization" => true,
        "constructor_arguments" => constructor_arguments
      }

      assert {:ok, %{abi: abi}} = Verifier.evaluate_authenticity(contract_address.hash, params)
      assert abi != nil
